document.getElementById("login-form").addEventListener("submit",(async e=>{e.preventDefault();try{const response=await fetch("../../../api/login/admin/login.php",{method:"POST",body:new FormData(e.target)}),data=await response.json();data.status?Swal.fire({title:"Login Successfully!",text:data.message,icon:data.icon,timer:3e3,showConfirmButton:!1,didClose:()=>{window.location.href=data.redirect}}):Swal.fire({title:"Error!",text:data.message,icon:data.icon,confirmButtonColor:"#3085d6",confirmButtonText:"Try Again"})}catch(error){console.log(error),Swal.fire({title:"Error!",text:"An unexpected error occurred. Please try again.",icon:"error",confirmButtonColor:"#3085d6",confirmButtonText:"OK"})}}));