document.addEventListener("DOMContentLoaded",(function(){const loginForm=document.querySelector("form"),emailInput=document.getElementById("email"),passwordInput=document.getElementById("password"),rememberBox=document.getElementById("remember");loginForm.addEventListener("submit",(async function(e){if(e.preventDefault(),!emailInput.value||!passwordInput.value)return void Swal.fire({title:"Error!",text:"Please fill in all fields",icon:"error",confirmButtonText:"OK"});if(/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(emailInput.value))try{const submitButton=loginForm.querySelector('button[type="submit"]');submitButton.innerHTML;submitButton.innerHTML='<i class="fas fa-spinner fa-spin mr-2"></i>Logging in...',submitButton.disabled=!0;const response=await fetch("../api/student/login.php",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({email:emailInput.value,password:passwordInput.value,remember:rememberBox.value})}),data=await response.json();"success"===data.status?Swal.fire({title:"Success!",text:data.message,icon:"success",timer:3e3,showConfirmButton:!1}).then((()=>{window.location.href="./dashboard/"})):Swal.fire({title:"Login Failed",text:data.message||"An error occurred during login",icon:"error",confirmButtonText:"Try Again"})}catch(error){console.error("Login error:",error),Swal.fire({title:"Error!",text:error,icon:"error",confirmButtonText:"OK"})}finally{const submitButton=loginForm.querySelector('button[type="submit"]');submitButton.innerHTML='<i class="fas fa-sign-in-alt mr-2"></i>Access Portal',submitButton.disabled=!1}else Swal.fire({title:"Error!",text:"Please enter a valid email address",icon:"error",confirmButtonText:"OK"})}));document.querySelectorAll("input").forEach((input=>{input.addEventListener("focus",(()=>{input.parentElement.classList.add("scale-[1.02]")})),input.addEventListener("blur",(()=>{input.parentElement.classList.remove("scale-[1.02]")}))}))}));